mapper.select:
	/** kr.kyoungjin.dataobject.dao.BoardDao.selectBoardList  */
	SELECT 
  			b.*
	 FROM (
			SELECT 
			    	@RNUM:=@RNUM+1 AS ROWNUM , 
			    	a.*
			 FROM 
			    (SELECT @RNUM:=0) R, 
			    (
			    	
			    )a 
	) as b

mapper.insert:
/** kr.kyoungjin.dataobject.dao.BoardDao.insertBoard */
INSERT INTO T_BOARD (
	  BOARD_CD
	, TITLE
	, BODY
	, REG_ID
	, REG_DATE
	, TAG
	, HTML_YN
)
VALUES(
	  #{boardCd}
	, #{title} 
	, #{body} 
	, #{regId}
	, NOW()
	, #{tag}
	, #{htmlYn}
)
 

	SELECT LAST_INSERT_ID()

mapper.update:
/** kr.kyoungjin.dataobject.dao.BoardDao.updateBoard */
UPDATE T_BOARD
  SET TITLE = #{title}
    , BODY = #{body}
    , TAG = #{tag}
    , HTML_YN = #{htmlYn}
    , UPT_ID = #{uptId}
    , UPT_DATE = NOW()
WHERE BOARD_SEQ = #{boardSeq}
