mapper.select:
	/** kr.kyoungjin.dataobject.dao.BoardMngDao.selectBoardMngList  */
	SELECT 
  			b.*
	 FROM (
			SELECT 
			    	@RNUM:=@RNUM+1 AS ROWNUM , 
			    	a.*
			 FROM 
			    (SELECT @RNUM:=0) R, 
			    (
			    	
			    )a 
	) as b

mapper.insert:
/** kr.kyoungjin.dataobject.dao.BoardMngDao.insertBoardMng */
INSERT INTO T_BOARD_MNG ( 
	  BOARD_CD
	, BOARD_NM
	, LIST_ROW_CNT
	, LIST_BLOCK_CNT
	, TITLE_SPLIT_LEN
	, CONTENTS_SPLIT_LEN
	, USE_YN
	, REG_DATE
	, REG_ID
)
VALUES(
	  #{boardCd}
	, #{boardNm} 
	, #{listRowCnt} 
	, #{listBlockCnt} 
	, #{titleSplitLen}
	, #{contentsSplitLen}
	, #{useYn}  
	, NOW()
	, #{regId}
 )
