mapper.select:
	/** kr.kyoungjin.dataobject.dao.BoardMngDao.selectBoardMngList  */
	SELECT 
  			b.*
	 FROM (
			SELECT 
			    	@RNUM:=@RNUM+1 AS ROWNUM , 
			    	a.*
			 FROM 
			    (SELECT @RNUM:=0) R, 
			    (
			    	
			    )a 
	) as b

mapper.insert:
/** kr.kyoungjin.dataobject.dao.BoardMngDao.insertBoardMng */
INSERT INTO T_BOARD_MNG ( 
	  BOARD_CD
	, BOARD_NM
	, LIST_ROW_CNT
	, LIST_BLOCK_CNT
	, TITLE_SPLIT_LEN
	, CONTENTS_SPLIT_LEN
	, USE_YN
	, REG_DATE
	, REG_ID
	, DEL_YN
)
VALUES(
	  #{boardCd}
	, #{boardNm} 
	, #{listRowCnt} 
	, #{listBlockCnt} 
	, #{titleSplitLen}
	, #{contentsSplitLen}
	, #{useYn}  
	, NOW()
	, #{regId}
	, 'N'
 )

mapper.update:
/** kr.kyoungjin.dataobject.dao.BoardMngDao.BoardMngVo */
UPDATE T_BOARD_MNG
  SET BOARD_NM = #{boardNm}
    , LIST_ROW_CNT = #{listRowCnt}
    , LIST_BLOCK_CNT = #{listBlockCnt}
    , TITLE_SPLIT_LEN = #{titleSplitLen}
    , CONTENTS_SPLIT_LEN = #{contentsSplitLen}
    , USE_YN = #{useYn}
    , UPT_DATE = NOW()
    , UPT_ID = #{uptId}
WHERE BOARD_CD = #{boardCd}
